    borderRadius: 16,
  },
  row: {
    flexDirection: 'row',
  },
  cell: {
    width: 48,
    height: 48,
    margin: 4,
    backgroundColor: '#444',
    borderRadius: 24,
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: 2,
    borderColor: 'transparent',
  },
  selectedCell: {
    borderColor: '#fff',
  },
  emoji: {
    fontSize: 32,
  },
  info: {
    color: '#aaa',
    marginTop: 24,
    fontSize: 16,
  },
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0,0,0,0.7)',
    alignItems: 'center',
    justifyContent: 'center',
  },
  gameOverText: {
    color: '#fff',
    fontSize: 28,
    fontWeight: 'bold',
    marginBottom: 24,
    textAlign: 'center',
  },
  mission: {
    color: '#ffd700',
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
    textAlign: 'center',
  },
  walletOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(20,20,30,0.98)',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 10,
  },
  walletTitle: {
    color: '#ffd700',
    fontSize: 26,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  walletRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  walletToken: {
    fontSize: 28,
    width: 40,
    textAlign: 'center',
  },
  walletAmount: {
    color: '#fff',
    fontSize: 20,
    width: 80,
    textAlign: 'center',
  },
  walletPrice: {
    color: '#0f0',
    fontSize: 18,
    width: 100,
    textAlign: 'center',
  },
  walletTotal: {
    color: '#fff',
    fontSize: 20,
    fontWeight: 'bold',
    marginVertical: 16,
  },
  walletButtons: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    width: '100%',
